        private static void DisplayMainChecklist()
        {
            Console.Clear();
            AnsiConsole.Write(new FigletText("---------------").Centered().Color(Color.Orange1));
            AnsiConsole.Write(new FigletText("Quest checklist").Centered().Color(Color.Aqua));
            AnsiConsole.Write(new FigletText("---------------").Centered().Color(Color.Orange1));
            var inputContinue = AnsiConsole.Prompt(new SelectionPrompt<string>()
               .Title("Before you can crack the alien pirate safe you need to train. \r\nPlease select a training activity")
               .AddChoices(new[]
               {
                    "Working under pressure",
                    "Divine guesswork",
                    "Navigating in the dark",
                    "Focus focus focus",
                    "Hacking practice",
                    "I HAVE THE CODE!!!!!!"
               }));

            if (inputContinue == "Working under pressure") 
            {
                StartTimeTest();
            }
            else if (inputContinue == "Divine guesswork")
            {
                NumberPredictionStart();
            }
            else if (inputContinue == "Navigating in the dark")
            {
                MazeMenu();
            }
            else if (inputContinue == "Focus focus focus")
            {
                FocusStart2();
            }
            else if (inputContinue == "Hacking practice")
            {
                LogicalReasoning();
            }
            else if (inputContinue == "I HAVE THE CODE!!!!!!")
            {
                PlaySecondMovie(); 
            }
        }
