                var steps = 0;
                var paths = new List<(int x, int y)>() { (currentX, currentY) };
                var nextPaths = new List<(int x, int y)>();
                while (paths.Count > 0)
                {
                    foreach (var path in paths)
                    {
                        if (processedMaze[(path.x, path.y)].Visibility == 0)
                        {
                            processedMaze[(path.x, path.y)].Visibility = 1d - (factor * steps);
                            if (!processedMaze[(path.x, path.y)].IsWall && steps < displayRadius)
                            {
                                for (int i = -1; i <= 1; i++)
                                {
                                    for (int j = -1; j <= 1; j++)
                                    {
                                        if (i == 0 && j == 0)
                                        {
                                            continue;
                                        }
                                        if (processedMaze.ContainsKey((path.x + i, path.y + j)))
                                        {
                                            nextPaths.Add((path.x + i, path.y + j));
                                        }
                                    }
                                }
                            }
                        }
                    }
                    steps++;
                    paths = nextPaths;
                    nextPaths = new List<(int x, int y)>();
                }
                //Display what you can
                for (int y = -displayRadius; y <= displayRadius; y++)
                {
                    for (int x = -displayRadius; x <= displayRadius; x++)
                    {
                        var mazeX = x + currentX;
                        var mazeY = y + currentY;
                        var chara = "^";
                        var visibility = 1d;
                        if (processedMaze.ContainsKey((mazeX, mazeY)))
                        {
                            var matchingCell = processedMaze[(mazeX, mazeY)];
                            if (y == 0 && x == 0)
                            {
                                chara = "@";
                            }
                            else if (!matchingCell.IsWall)
                            {
                                chara = ".";
                            }else
                            {
                                chara = "#";
                            }
                            visibility = matchingCell.Visibility;
                            Console.SetCursorPosition(startX + x, startY + y);
                            AnsiConsole.Write(new Markup(chara, new Style(new Color((byte)(255 * visibility), (byte)(255 * visibility), (byte)(255 * visibility)))));
                        }
                        else
                        {
                            Console.SetCursorPosition(startX + x, startY + y);
                            AnsiConsole.Write(new Markup(chara, new Style(new Color(0, 50, 255))));
                        }
                    }
                }

                ConsoleKey currentKey = ConsoleKey.P;
                do
                {
                    while (!Console.KeyAvailable)
                    {
                        Thread.Sleep(50);
                    }
                    currentKey = Console.ReadKey(true).Key;
                } while (!new ConsoleKey[] { ConsoleKey.A, ConsoleKey.W, ConsoleKey.S, ConsoleKey.D }.Contains(currentKey));

                var nextX = currentX;
                var nextY = currentY;
                switch (currentKey)
                {
                    case ConsoleKey.A:
                        nextX = nextX - 1;
                        break;
                    case ConsoleKey.D:
                        nextX = nextX + 1;
                        break;
                    case ConsoleKey.W:
                        nextY = nextY - 1;
                        break;
                    case ConsoleKey.S:
                        nextY = nextY + 1;
                        break;
                }
                if (processedMaze.ContainsKey((nextX, nextY)) && !processedMaze[(nextX, nextY)].IsWall)
                {
                    currentX = nextX;
                    currentY = nextY;
                }
            }

            Console.Clear();
            AnsiConsole.Write(new FigletText(" ").LeftJustified().Color(Color.Aqua));
            AnsiConsole.Write(new FigletText(" ").LeftJustified().Color(Color.Aqua));
            AnsiConsole.Write(new FigletText("!!CONGRATULATIONS!!").Centered().Color(Color.Aqua));
            Thread.Sleep(3000);
            Console.Clear();
            AnsiConsole.Write(new FigletText("---------------").Centered().Color(Color.Orange1));
            AnsiConsole.Write(new FigletText("1842").Centered().Color(Color.Aqua));
            AnsiConsole.Write(new FigletText("---------------").Centered().Color(Color.Orange1));
            Thread.Sleep(5000);
            Console.CursorVisible = true;
            DisplayMainChecklist();
        }


        protected static string clearBuffer = null; // Clear this if window size changes
        protected static void ClearConsole()
        {
            if (clearBuffer == null)
            {
                var line = "".PadLeft(Console.WindowWidth, ' ');
                var lines = new StringBuilder();

                for (var i = 0; i < Console.WindowHeight; i++)
                {
                    lines.AppendLine(line);
                }

                clearBuffer = lines.ToString();
            }

            Console.SetCursorPosition(0, 0);
            Console.Write(clearBuffer);
            Console.SetCursorPosition(0, 0);
        }

