        private static void PlayLoadMovie()
        {
            Console.Clear();
            int waitcount = 100;
            AnsiConsole.Progress()
            .Start(ctx =>
            {
                // Define tasks
                var task1 = ctx.AddTask("[green]Reticulating splines[/]");
                var task2 = ctx.AddTask("[green]Folding space[/]");
                var task3 = ctx.AddTask("[green]Torquing sprockets[/]");
                var task4 = ctx.AddTask("[green]Calcifying vectors[/]");
                var task5 = ctx.AddTask("[green]Observing manifestations[/]");

                while (!ctx.IsFinished)
                {
                    if (task1.Value == 99 && waitcount > 0)
                    {
                        waitcount--;
                    }
                    else
                    {
                        task1.Increment(1.5);
                    }
                    task2.Increment(0.5);
                    task3.Increment(0.8);
                    task4.Increment(1.2);
                    task5.Increment(0.2);
                    Thread.Sleep(50);
                }
            });
            PlayIntroMovie();
        }

