            var splitMaze = maze.Split(new string[] { "\n", "\r" }, StringSplitOptions.RemoveEmptyEntries);
            var processedMaze = new Dictionary<(int x, int y),MazeSquare>();
            for (int y = 0; y < splitMaze.Length; y++)
            {
                for (int  x = 0; x < splitMaze[y].Length; x++)
                {
                    if (splitMaze[y][x] == '#')
                    {
                        processedMaze.Add((x, y), new MazeSquare()
                        {
                            X = x,
                            Y = y,
                            IsWall = true
                        });
                    }
                    if (splitMaze[y][x] == '.')
                    {
                        processedMaze.Add((x, y), new MazeSquare()
                        {
                            X = x,
                            Y = y,
                            IsWall = false
                        });
                    }
                }
            }

            var currentX = 0;
            var currentY = 0;
            var goalX = splitMaze.Last().Length - 1;
            var goalY = splitMaze.Length - 1;

            while (currentX != goalX || currentY != goalY)
            {
                ClearConsole();
                //Clear the maze
                foreach (var item in processedMaze)
                {
                    item.Value.Visibility = 0;
                }

                //Calculate Visibility
                var steps = 0;
                var paths = new List<(int x, int y)>() { (currentX, currentY) };
                var nextPaths = new List<(int x, int y)>();
                while (paths.Count > 0)
                {
                    foreach (var path in paths)
                    {
                        if (processedMaze[(path.x, path.y)].Visibility == 0)
                        {
                            processedMaze[(path.x, path.y)].Visibility = 1d - (0.2d * steps);
                            if (!processedMaze[(path.x, path.y)].IsWall && steps < 5)
                            {
                                for (int i = -1; i <= 1; i++)
                                {
                                    for (int j = -1; j <= 1; j++)
                                    {
                                        if (i == 0 && j == 0)
                                        {
                                            continue;
                                        }
                                        if (processedMaze.ContainsKey((path.x + i, path.y + j)))
                                        {
                                            nextPaths.Add((path.x + i, path.y + j));
                                        }
                                    }
                                }
                            }
                        }
                    }
                    steps++;
                    paths = nextPaths;
                    nextPaths = new List<(int x, int y)>();
                }
                var currentLineText = "";
                //Display what you can
                for (int y = 0; y < splitMaze.Length; y++)
                {
                    for (int x = 0; x < splitMaze[y].Length; x++)
                    {
                        var chara = ".";
                        if (x == currentX && y == currentY)
                        {
                            chara = "@"; 
                        }else if (processedMaze[(x, y)].IsWall)
                        {
                            chara = "#";
                        }
                        if (processedMaze[(x, y)].Visibility == -1)
                        {
                            currentLineText += " ";
                        }
                        else
                        {
                            if (currentLineText.Length > 0)
                            {
                                Console.Write(currentLineText);
                                currentLineText = "";
                            }
                            AnsiConsole.Write(new Markup(chara, new Style(new Color((byte)(255 * processedMaze[(x, y)].Visibility), (byte)(255 * processedMaze[(x, y)].Visibility), (byte)(255 * processedMaze[(x, y)].Visibility)))));
                        }
                    }
                    currentLineText += "\r\n";
                }
                if (currentLineText.Length > 0)
                {
                    Console.Write(currentLineText);
                }

                ConsoleKey currentKey = ConsoleKey.P;
                do
                {
                    while (!Console.KeyAvailable)
                    {
                        Thread.Sleep(50);
                    }
                    currentKey = Console.ReadKey(true).Key;
                } while (!new ConsoleKey[] { ConsoleKey.A, ConsoleKey.W, ConsoleKey.S, ConsoleKey.D }.Contains(currentKey));

                var nextX = currentX;
                var nextY = currentY;
                switch (currentKey)
                {
                    case ConsoleKey.A:
                        nextX = nextX - 1;
                        break;
                    case ConsoleKey.D:
                        nextX = nextX + 1;
                        break;
                    case ConsoleKey.W:
                        nextY = nextY - 1;
                        break;
                    case ConsoleKey.S:
                        nextY = nextY + 1;
                        break;
                }
                if (processedMaze.ContainsKey((nextX, nextY)) && !processedMaze[(nextX, nextY)].IsWall)
                {
                    currentX = nextX;
                    currentY = nextY;
                }
            }

            Console.Clear();
            AnsiConsole.Write(new FigletText(" ").LeftJustified().Color(Color.Aqua));
            AnsiConsole.Write(new FigletText(" ").LeftJustified().Color(Color.Aqua));
            AnsiConsole.Write(new FigletText("!!CONGRATULATIONS!!").Centered().Color(Color.Aqua));
